jQuery(function(){jQuery(window).mouseup(function(b){var a=jQuery.getSelection();if(a.text.length>0){jQuery(b.target).trigger("pageselect",[a])}})});jQuery.getSelection=function(){if(window.getSelection){var a=window.getSelection();return{text:a.toString(),range:a.getRangeAt(0)}}else{if(document.getSelection){return document.getSelection()+""}else{if(document.selection){return document.selection.createRange().text+""}}}};DocumentFragment.prototype.html=function(){html="";jQuery(this.childNodes).each(function(){html+=this.innerHTML});return html};